var ports = msg.payload;

// Crear un string con los puertos separados por un salto de línea
const portString = ports.map(port => `Puerto ${port.port} con protocolo ${port.protocol}`).join('<br>');

msg.payload = portString;

return msg;

var reglas = msg.payload;
var soloReglas = msg.payload.slice(1);

if (reglas[0].Estado == "active") {
  if (soloReglas.length == 0) {
    msg.payload = 'No hay reglas añadidas (Comportamiento por defecto).';
  } else {
    msg.payload = soloReglas.map(rule => `Regla ${rule.rule_number}: ${rule.rule_info}`).join('<br>');
  }
} else {
    msg.payload = 'UFW no está activo.';
}

return msg;

